import React from 'react';
import { View, Text, Image, TouchableOpacity } from 'react-native';
// import { createStackNavigator } from '@react-navigation/stack';
// import { NavigationContainer } from '@react-navigation/native';
import { useNavigation } from '@react-navigation/native';


const WelcomeScreen = ({ navigation }) => {
  const handleLoginPress = () => {
    navigation.navigate('Connect'); // ניווט לקומפוננטה Connect
  };

  const handleCreateAccountPress = () => {
    navigation.navigate('AddVolunteer'); // ניווט לקומפוננטה AddVolunteer
  };

  return (
    <View style={styles.container}>
      {/* <Image source={require('./assets/logo.png')} style={styles.logo} /> */}
      <Image source={require('/images/logo.png')} style={styles.logo} />
      <Text style={styles.welcomeText}>ברוכים הבאים למתחברים למתנדבים</Text>
      <TouchableOpacity style={styles.button} onPress={handleLoginPress}>
        <Text style={styles.buttonText}>התחבר</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={handleCreateAccountPress}>
        <Text style={styles.buttonText}>יצירת חשבון</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = {
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#FFFFFF',
  },
  logo: {
    width:200,
    height: 150,
    marginBottom: 20,
  },
  welcomeText: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 30,
    color: '#000000',
  },
  button: {
    backgroundColor: '#FFC0CB',
    paddingVertical: 10,
    paddingHorizontal: 20,
    marginBottom: 10,
    borderRadius: 5,
  },
  buttonText: {
    fontSize: 16,
    color: '#FFFFFF',
  },
};

export default WelcomeScreen;
