import React, { useState } from 'react';
import { View, Text, Image, TextInput, TouchableOpacity, Picker } from 'react-native';

const RegistrationForm = () => {
    const [volunteerId, setVolunteerId] = useState('');
    const [volunteerName, setVolunteerName] = useState('');
    const [phone, setPhone] = useState('');
    const [email, setEmail] = useState('');
    const [address, setAddress] = useState('');
    const [city, setCity] = useState('');
    const [fieldOfVolunteering, setFieldOfVolunteering] = useState('');
    const [branch, setBranch] = useState('');
    const [disabledVehicle, setDisabledVehicle] = useState('');

    const handleRegistration = () => {
        const volunteerData = {

            volunteerId: volunteerId,
            volunteerName: volunteerName,
            phone: phone,
            email: email,
            address: address,
            city: city,
            fieldOfVolunteering: fieldOfVolunteering,
            branch: branch,
            disabledVehicle: disabledVehicle

        };


        console.log(volunteerData);
        addVolunteer(basicUrl + 'volunteers/', volunteerData);
        // הכנסת הנתונים למסד הנתונים
        // כאן תוכל לכתוב קוד שמבצע את השליחה למסד הנתונים
        // לדוגמה, באמצעות REST API או GraphQL
    };

    return (
        <View style={styles.container}>
            <Image source={require('/images/logo.png')} style={styles.logo} />
            <Text style={styles.title}>הרשמה</Text>
            <TextInput
                style={styles.input}
                placeholder="שם משתמש"
                value={volunteerName}
                onChangeText={setVolunteerName}
            />
            <TextInput
                style={styles.input}
                placeholder="תעודת זהות"
                value={volunteerId}
                onChangeText={setVolunteerId}
            />
            <TextInput
                style={styles.input}
                placeholder="פלאפון"
                value={phone}
                onChangeText={setPhone}
            />
            <TextInput
                style={styles.input}
                placeholder="מייל"
                value={email}
                onChangeText={setEmail}
            />
            <TextInput
                style={styles.input}
                placeholder="כתובת"
                value={address}
                onChangeText={setAddress}
            />
            <TextInput
                style={styles.input}
                placeholder="עיר"
                value={city}
                onChangeText={setCity}
            />
            {/* <TextInput
        style={styles.input}
        placeholder="תחום התנדבות"
        value={volunteeringField}
        onChangeText={setVolunteeringField}
      />
      <TextInput */}
            <Picker
             style={styles.input}
                selectedValue={fieldOfVolunteering}
                onValueChange={(itemValue) => setFieldOfVolunteering(itemValue)}
            >
                <Picker.Item label="תחום התנדבות" value="" />
                <Picker.Item label="הסעות חולים" value="הסעות חולים" />
                <Picker.Item label="גיהוץ למשפחות" value="גיהוץ למשפחות" />
                <Picker.Item label="העברת חבילות" value="העברת חבילות" />
                <Picker.Item label="חלוקת אוכל" value="חלוקת אוכל" />
                <Picker.Item label="שמירה על ילדים בבתי חולים" value="שמירה על ילדים בבתי חולים" />
                <Picker.Item label="עריכת קניות למשפחותיהם" value="	עריכת קניות למשפחותיהם" />
                <Picker.Item label="השאלת ציוד רפואי" value="השאלת ציוד רפואי" />
                <Picker.Item label="סיוע לחולים בבתיהם " value="סיוע לחולים בבתיהם " />
                <Picker.Item label="סיעוד ותמיכה בקשישים" value="סיעוד ותמיכה בקשישים" />
            </Picker>

            {/* <TextInput
                style={styles.input}
                placeholder="סניף"
                value={branch}
                onChangeText={setBranch}
            /> */}
 <Picker
             style={styles.input}
                selectedValue={branch}
                onValueChange={(itemValue) => setBranch(itemValue)}
            >
                <Picker.Item label="סניף" value="" />
                <Picker.Item label="אשדוד" value="אשדוד" />
                <Picker.Item label="בני ברק" value="בני ברק" />
                <Picker.Item label="ירושלים" value="ירושלים" />
                <Picker.Item label="רחובות" value="רחובות" />
                <Picker.Item label="אלעד" value="אלעד" />
                <Picker.Item label="בית שמש" value="בית שמש" />
                <Picker.Item label="אשקלון" value="אשקלון" />
                <Picker.Item label="אופקים " value="אופקים " />
                <Picker.Item label="'קרית ספר" value="קרית ספר" />
                <Picker.Item label="חיפה" value="רחובות" />
                <Picker.Item label="פתח תקוה" value="אלעד" />
                <Picker.Item label="נתניה" value="בית שמש" />
                <Picker.Item label="באר שבע" value="אשקלון" />
                <Picker.Item label="ראשון לציון " value="אופקים " />
                <Picker.Item label="ביתר עילית" value="קרית ספר" />
                <Picker.Item label="ערד" value="רחובות" />
                <Picker.Item label="צפת" value="אלעד" />
                <Picker.Item label="מירון" value="בית שמש" />
                <Picker.Item label="קרית גת" value="אשקלון" />
                <Picker.Item label="ראשון לציון " value="אופקים " />
                <Picker.Item label="ביתר עילית" value="קרית ספר" />
            </Picker>



            <TextInput
                style={styles.input}
                placeholder="האם יש רכב נכים"
                value={disabledVehicle}
                onChangeText={setDisabledVehicle}
            />
            <TouchableOpacity style={styles.button} onPress={handleRegistration}>
                <Text style={styles.buttonText}>הרשמה</Text>
            </TouchableOpacity>
        </View>
    );
};

const styles = {
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    logo: {
        width: 300,
        height: 200,
        marginBottom: 20,
    },
    title: {
        fontSize: 24,
        fontWeight: 'bold',
        marginBottom: 20,
    },
    input: {
        width: '80%',
        height: 40,
        marginBottom: 10,
        borderWidth: 1,
        borderColor: '#ccc',
        padding: 10,
        text: 'rtl',
        borderRadius: 5, 
   
  },
    button: {
        width: '80%',
        height: 40,
        backgroundColor: '#FFC0CB',
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: 20,
        borderRadius: 5,
    },
    buttonText: {
        color: 'white',
        fontSize: 16,
        fontWeight: 'bold',
    },
};

export default RegistrationForm;
