import axios from 'axios';

// Example function to send email from the React Native app
const sendEmail = () => {
  const recipients = ['recipient1@example.com', 'recipient2@example.com'];
  const subject = 'Hello from React Native!';
  const body = '<h1>Hello!</h1><p>This is the content of the email.</p>';

  axios.post('http://your-node-js-server.com/send-email', {
    recipients,
    subject,
    body,
  })
  .then(response => {
    console.log('Email sent successfully:', response.data);
  })
  .catch(error => {
    console.error('Error sending email:', error);
  });
};

