import { addItem, getById, getAll, updateItem, addVolunteer } from './service'
import React, { useState,useEffect } from "react";
import axios from 'axios'
// import LoginScreen from '../uniteapp/login-screen/src/login/login';
import { StyleSheet, Text, View } from 'react-native';
import Connect from './components/Connect';
import HelpMassage from './components/HelpMassage';
import FormComponent from './components/FormComponent';
import VolunteeringInTherapy from './components/VolunteeringInTherapy';
import AddVolunteer from './components/AddVolunteer';
import SignupScreen from './src/screens/SignupScreen';
import VolunteerComponent from './components/VolunteerComponent'
import AddRecipient from './components/AddRecipient';
import Retrieve from './components/Retrieve'
import Table from './components/Table'
import LoginScreen from './components/LoginScreen'
import RegistrationForm from './components/RegistrationForm'
// import FetchDB from './components/FetchDB'

import LanguageSelection  from './components/LanguageSelection '
// import FetchFromMongoDB  from './components/FetchFromMongoDB '



import {AppRegistry} from 'react-native';
import thunk from 'redux-thunk';
import {Provider} from 'react-redux';
import {createStore, applyMiddleware} from 'redux';

import AppReducer from './src/reducers';
import AppNavigator from './src/navigations/AppNavigation';
import AllMessage from './components/AllMessage';

const store = createStore(AppReducer, applyMiddleware(thunk));

console.disableYellowBox = true;

function StarterApp() {
    const [details, setDetails] = useState([]);//砖住 转 转 注专 - 注爪 砖住 砖转 转  拽
  const [request, setRequest] = useState();//砖转    转 砖 住专住
  const [currentUserID, setCurrentUserID] = useState('');

  /*connect to service - node - 砖 住专住 */
  const getRequestHandler = async () => {
    const response = await axios.get('http://localhost:8000/volunteers')
      .then((response) => {
        // setName=(response.data.name)
        setDetails(response.data);
        setRequest("GET");
        console.log("Success!!!!!!!!", response)

      });
    /* */

  };

  useEffect(() => {
    // Simulating the retrieval of the current user's ID
    setCurrentUserID('222222222');
  }, []);


  return (
    <div className="App">
           <h1>My Site</h1>
          <button onClick={() => getRequestHandler()} >connect to node that works!</button>
          <hr />{/* 爪转 转 注 转 */}
           {request == "GET" ? <span> {details.ravkavId} Get!!!!!</span> : <span>None</span>}
          <View>
            {/* <AddVolunteer /> */}
            {/* <AddRecipient /> */}
            {/* {currentUserID === '222222222' ? (
        <HelpMassage />
      ) : (
        <Text>You do not have permission to access this component</Text>
      )} */}
            {/* <HelpMassage/> */}
            {/* <VolunteeringInTherapy/> */}
           {/* < BenodProvider > */}
              {/* <Connect /> */}
              {/* <Retrieve/> */}
              {/* <FetchDB/> */}
              {/* <Table/> */}
              <RegistrationForm/>
              {/* <LoginScreen/> */}
             {/* <AllMessage /> */}
             {/* <LanguageSelection /> */}
            {/* <VolunteerComponent /> */}
{/* </BenodProvider> */}
            {/* <FormComponent/> */}
            {/* <try/> */}
           {/* <StatusBar style="auto" /> */}
           <Provider store={store}>
      {/* <AppNavigator /> */}
      {/* <SignupScreen/> */}
     
      {/* <LoginScreen /> */}
    </Provider>
          </View>
         </div>
    
  );
}

AppRegistry.registerComponent('rn_starter_kit', () => StarterApp);

export default StarterApp;
