import React, { useState } from 'react';
import { StyleSheet, View, TextInput, Button,Text } from 'react-native';
import { Linking } from 'react-native';
import { basicUrl } from './basicUrl';
const {addVolunteer} = require('./service');

import axios from 'axios';
 
const volunteer = () => {
  const [volunteerId, setVolunteerId] = useState('');
  const [volunteerName, setVolunteerName] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [fieldOfVolunteering, setFieldOfVolunteering] = useState('');
  const [branch, setBranch] = useState('');
  const [disabledVehicle, setDisabledVehicle] = useState('');


  const handleSignup = () => {
    const volunteerData = {

        volunteerId: volunteerId,
        volunteerName: volunteerName,
        phone: phone,
        email:email,
        address: address,     
        city: city,
        fieldOfVolunteering: fieldOfVolunteering,
        branch: branch,
        disabledVehicle: disabledVehicle

    };
    
    // כאן יכול להיות קוד לשליחת המידע לשרת ועיבודו
      // getData(basicUrl);
      // addItem(url, {id: 1, name: 'Leanne Graham', username: 'Bret'})
      // addUser(basicUrl+'users/',{'id':5,'name':'pnina'});
      addVolunteer(basicUrl+'volunteers/',volunteerData);
    }

//   const handlePress = async () => {
//     const url = 'https://moovitapp.com';
//     const supported = await Linking.canOpenURL(url);

//     if (supported) {
//       await Linking.openURL(url);
//     } else {
//       console.log(`Don't know how to open this URL: ${url}`);
//     }
//   };

  return (
        
    <View style={styles.container}>
      {/* <Image source={require('./path/to/image.png')} style={styles.logo} /> */}
      <br></br>
      <Button title="הכנס/י פרטים אישיים"/>
      <br></br>
      <TextInput
        style={styles.input}
        placeholder="תעודת זהות"
        value={volunteerId}
        onChangeText={setVolunteerId}
      />
      <TextInput
        style={styles.input}
        placeholder="שם מלא"
        value={volunteerName}
        onChangeText={setVolunteerName}
      />
      <TextInput
        style={styles.input}
        placeholder="פלאפון"
        value={phone}
        onChangeText={setPhone}
      />
     
      <TextInput
        style={styles.input}
        placeholder="אימייל"
        value={email}
        onChangeText={setEmail}
        // keyboardType="מס' קו"
      />
       <TextInput
        style={styles.input}
        placeholder="כתובת"
        value={laddressine}
        onChangeText={setAddress}
      />
      <TextInput
        style={styles.input}
        placeholder="עיר"
        value={city}
        onChangeText={setCity}
      />
      <TextInput
        style={styles.input}
        placeholder="תחום התנדבות"
        value={fieldOfVolunteering}
        onChangeText={setFieldOfVolunteering}
      />
        <TextInput
        style={styles.input}
        placeholder="סניף"
        value={branch}
        onChangeText={setLine}
      />
        <TextInput
        style={styles.input}
        placeholder="האם יש רכב נכים"
        value={line}
        onChangeText={setLine}
      />
      
      <br></br>
      <Button title="הירשם" onPress={handleSignup} />
      {/* <br></br>
      <Button title="עבור ל-moovit" onPress={handlePress}/> */}
   
    </View>
  );
          

    
};

const styles = StyleSheet.create({
  container: {
    // flex: 1,
    // top:'25%',
    // // textAlign:'center',
    // left:'20%',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#fff',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    justifyContent: 'space-around',
    backgroundColor:'hsl(124, 93%, 61%);'
  },
  input: {
    width: '80%',
    padding: 10,
    marginVertical: 10,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    // direction:'rtl',
    textAlign:'center',
    // fontSize:'Fantasy  ',
    fontWeight: 'bold'

  },
  button:{
  width: '80px',
  height:'80px'
  },

});
export default volunteer;
