            {/* <VolunteerComponent /> */}
{/* </BenodProvider> */}
            <FormComponent/>
            {/* <try/> */}
           {/* <StatusBar style="auto" /> */}
           <Provider store={store}>
      {/* <AppNavigator /> */}
      {/* <SignupScreen/> */}
     
      {/* <LoginScreen /> */}
    </Provider>
          </View>
         </div>
    
  );
}

AppRegistry.registerComponent('rn_starter_kit', () => StarterApp);

export default StarterApp;



