import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';

const checked=false
const rejection=false


const HelpMassage = () => {

    const handleAccept = () => {
        // פעולות שייעשו בעת לחיצה על כפתור אישור
        // ניתן להשתמש כאן ב-fetch לשליפת הנתונים מ-MongoDB
      
        const navigation = useNavigation();
        navigation.navigate('SuccessScreen', { message: 'ההתנדבות הושלמה בהצלחה, הפרטים ישלחו בהודעה נפרדת' });
      }

  const handleReject = () => {
    // פעולות שייעשו בעת לחיצה על כפתור דחייה
    // ניתן להשתמש כאן ב-fetch לשליפת הנתונים מ-MongoDB
    rejection=true;
  
  }

  return (
    <View style={styles.container}>
     <Text>{`Hello, ${name}!`}</Text>
     <Text>{`צריכים, ${name}מ`}</Text>


      <Text style={styles.message}>צריכים הסעה מאשדוד הלל 8 לבית החולים אסותא אשדוד בשעה 16:15 יום שלישי 16/02/2023</Text>
      {/* <Text style={styles.message}>צריכים הסעה מאשדוד הלל 8 לבית החולים אסותא אשדוד בשעה 16:15 יום שלישי 16/02/2023</Text> */}
      <View style={styles.buttonContainer}>
      <TouchableOpacity style={styles.rejectButton} onPress={handleReject}>
          <Text style={styles.buttonText}>דחייה</Text>
          if(rejection==true?<massage/>:"");
        </TouchableOpacity>
      
      
        <TouchableOpacity style={styles.acceptButton} onPress={handleAccept}>
          <Text style={styles.buttonText}>אישור</Text>
        </TouchableOpacity>
      
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#F0F8FF',
    borderRadius: 10,
    padding: 10,
    margin: 10,
    alignItems: 'center'
  },
  message: {
    fontSize: 20,
    fontWeight: 'bold',
    textAlign: 'center',
    color: '#4B0082'
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginTop: 10
  },
  acceptButton: {
    backgroundColor: '#00CED1',
    padding: 10,
    borderRadius: 10,
    width: 120,
    alignItems: 'center'
  },
  rejectButton: {
    backgroundColor: '#FF6347',
    padding: 10,
    borderRadius: 10,
    width: 120,
    alignItems: 'center'
  },
  buttonText: {
    color: '#FFFFFF',
    fontWeight: 'bold',
    fontSize: 16
  }
});

export default HelpMassage;


