const EmailClient = () => {
  const sendEmail = () => {
    Linking.openURL('mailto:et3252760@gmail.com?subject=Hello%20from%20React%20Native&body=This%20is%20the%20body%20of%20the%20email');
  };

  return (
    <View style={{ flex: 1 }}>
      {/* <Image
        source={require('/images/logo.png')}
        style={{ width: '100%', height: 200 }}
        resizeMode="cover"
      /> */}
      <Image
          source={require('/images/logo.png')}
          style={styles.image}
        />

      <View style={{ margin: 20 }}>
        <Button title="Send Email" onPress={sendEmail} />
      </View>
    </View>
  );
};
const styles = StyleSheet.create({
   
});
export default EmailClient;
