import React, { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [volunteeringData, setVolunteeringData] = useState(null);

  useEffect(() => {
    // Fetch data from MongoDB and set it to the state
    const fetchData = async () => {
      const result = await axios.get("https://my-mongo-db-api.com/volunteering-data");
      setVolunteeringData(result.data);
    };
    fetchData();
  }, []);

  return (
    <div style={{ margin: 20 }}>
      {volunteeringData && (
        <div>
          <h1>Volunteering Details:</h1>
          <p>Volunteering: {volunteeringData.volunteering}</p>
          <p>Location: {volunteeringData.location}</p>
          <p>From: {volunteeringData.from}</p>
          <p>To: {volunteeringData.to}</p>
          <p>Date: {volunteeringData.date}</p>
          <p>Day: {volunteeringData.day}</p>
          <div style={{ display: "flex", justifyContent: "space-between" }}>
            <button style={{ padding: 10, backgroundColor: "green", color: "white", marginRight: 10 }}>Approve</button>
            <button style={{ padding: 10, backgroundColor: "red", color: "white" }}>Decline</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
