import React from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';

const MyComponent = () => {
  return (
    <View style={styles.container}>
      <View style={styles.imageContainer}>
        <Image
          source={require('./path/to/image.jpg')}
          style={styles.image}
        />
      </View>
      <View style={styles.textContainer}>
        <Text style={styles.text}>גיהוץ למשפחות</Text>
        <Text style={styles.text}>הסעת חולים</Text>
        <Text style={styles.text}>העברת חבילות</Text>
        <Text style={styles.text}>קניות למשפחה</Text>
        <Text style={styles.text}>שמירה על ילדים בבתי חולים</Text>
        <Text style={styles.text}>סיוע למבוגרים</Text>
        <Text style={styles.text}>שמירה במחלקות שונות</Text>
        <Text style={styles.text}>הסעת נכים</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  imageContainer: {
    alignItems: 'center',
    marginTop: 20,
  },
  image: {
    width: 200,
    height: 200,
    resizeMode: 'contain',
  },
  textContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  text: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
});

export default MyComponent;


