import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';

const LanguageSelection = () => {
  const [selectedLanguage, setSelectedLanguage] = useState('');
  const [firstAidKnowledge, setFirstAidKnowledge] = useState('');

  const handleLanguageSelect = (language) => {
    setSelectedLanguage(language);
  };

  const handleFirstAidKnowledgeSelect = (knowledge) => {
    setFirstAidKnowledge(knowledge);
  };

  return (
    <View style={styles.container}>
    
      <View style={styles.optionsContainer}>
        <Text style={styles.title}>כדי לייעל את הפעילות שלנו, דרג את ההעדפות והמיומנויות שלך</Text>
        <Text style={styles.subtitle}>בחר אחת מהאפשרויות הבאות:</Text>
        <Text style={styles.title}>כיצד אתה דובר אנגלית</Text>
        {/* <Text style={styles.subtitle}>כיצד אתה דובר אנגלית</Text> */}
        <TouchableOpacity
          style={[styles.option, selectedLanguage === 'שפת אם' && styles.selectedOption]}
          onPress={() => handleLanguageSelect('שפת אם')}
        >
          <Text style={styles.optionText}>שפת אם</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.option, selectedLanguage === 'שפת אם' && styles.selectedOption]}
          onPress={() => handleLanguageSelect('שפת אם')}
        >
          <Text style={styles.optionText}>שפת אם</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.option, selectedLanguage === 'שפת אם' && styles.selectedOption]}
          onPress={() => handleLanguageSelect('שפת אם')}
        >
          <Text style={styles.optionText}>שפת אם</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.option, selectedLanguage === 'שפה נוספת' && styles.selectedOption]}
          onPress={() => handleLanguageSelect('שפה נוספת')}
        >
          <Text style={styles.optionText}>שפה נוספת</Text>
        </TouchableOpacity>

        <TouchableOpacity
        style={[styles.option, selectedLanguage === 'רמה טובה' && styles.selectedOption]}
        onPress={() => handleLanguageSelect('רמה טובה')}
      >
        <Text style={styles.optionText}>רמה טובה</Text>
      </TouchableOpacity>


        <TouchableOpacity
        style={[styles.option, selectedOption === 'שפת אם' && styles.selectedOption]}
        onPress={() => handleOptionSelect('שפת אם')}
      >
        <Text style={styles.optionText}>שפת אם</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={[styles.option, selectedOption === 'שפה נוספת' && styles.selectedOption]}
        onPress={() => handleOptionSelect('שפה נוספת')}
      >
        <Text style={styles.optionText}>שפה נוספת</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={[styles.option, selectedOption === 'רמה טובה' && styles.selectedOption]}
        onPress={() => handleOptionSelect('רמה טובה')}
      >
        <Text style={styles.optionText}>רמה טובה</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={[styles.option, selectedOption === 'רמה בינונית' && styles.selectedOption]}
        onPress={() => handleOptionSelect('רמה בינונית')}
      >
        <Text style={styles.optionText}>רמה בינונית</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={[styles.option, selectedOption === 'רמה בסיסית' && styles.selectedOption]}
        onPress={() => handleOptionSelect('רמה בסיסית')}
      >
        <Text style={styles.optionText}>רמה בסיסית</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={[styles.option, selectedOption === 'לא דובר' && styles.selectedOption]}
        onPress={() => handleOptionSelect('לא דובר')}
      >
        <Text style={styles.optionText}>לא דובר</Text>
      </TouchableOpacity>
        {/* Add more language options with TouchableOpacity for other choices */}
      </View>

      <Text style={styles.categoryTitle}>האם יש לך ידע בעזרה ראשונה</Text>
      <View style={styles.optionsContainer}>
        <TouchableOpacity
          style={[styles.option, firstAidKnowledge === 'מע"ר' && styles.selectedOption]}
          onPress={() => handleFirstAidKnowledgeSelect('מע"ר')}
        >
          <Text style={styles.optionText}>מע"ר</Text>
        </TouchableOpacity>
        {/* Add more first aid knowledge options with TouchableOpacity for other choices */}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#F5F5F5',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
    textAlign: 'center',
    color: '#333',
  },
  optionsContainer: {
    alignItems: 'center',
    justifyContent: 'center',
  },
  subtitle: {
    fontSize: 16,
    marginBottom: 10,
    textAlign: 'center',
    color: '#666',
  },
  categoryTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginTop: 20,
    marginBottom: 10,
    textAlign: 'center',
    color: '#333',
  },
  option: {
    borderWidth: 1,
    borderColor: '#CCC',
    borderRadius: 10,
    padding: 10,
    marginVertical: 5,
    width: 200,
    alignItems: 'center',
    justifyContent: 'center',
  },
  selectedOption: {
    backgroundColor: '#007AFF',
    borderColor: '#007AFF',
  },
  optionText: {
    fontSize: 16,
    color: '#333',
  },
});

export default LanguageSelection;
