  }

  const fetchData = async () => {
    try {
      // Perform an HTTP request to fetch data from the server
      // const response = await fetch('http://example.com/api/data');
      const response = await fetch('http://localhost:8000/');
      
      // Parse the response as JSON
      const jsonData = await response.json();
      
      // Set the retrieved data in the state
      setData(jsonData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };


  // {!rejection && !checked ? <span>!rejection && !checked</span> : <span>ddd</span>}
  return (
    <>
    {!rejection && !checked ?  
    <View style={styles.container}>
      {/* <Text>{`Hello, ${name}!`}</Text> */}
      <Text style={styles.message}>{`צריכים ${fieldOfVolunteering} מ${from} ל${where} בשעה${time} יום${day} ${date} `}</Text>


