import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';

const LanguageSelection = () => {
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedAnswer, setSelectedAnswer] = useState('');

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setSelectedAnswer('');
  };

  const handleAnswerSelect = (answer) => {
    setSelectedAnswer(answer);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>כיצד אתה דובר אנגלית</Text>
      <View style={styles.optionsContainer}>
        <Text style={styles.subtitle}>כדי לייעל את הפעילות שלנו, דרג את ההעדפות והמיומנויות שלך</Text>
        <Text style={styles.subtitle}>בחר אחת מהאפשרויות הבאות:</Text>
        <TouchableOpacity
          style={[styles.option, selectedCategory === 'שפת אם' && styles.selectedOption]}
          onPress={() => handleCategorySelect('שפת אם')}
        >
          <Text style={styles.optionText}>שפת אם</Text>
        </TouchableOpacity>
        {/* Add more category options with TouchableOpacity */}
      </View>

      {selectedCategory === 'שפת אם' && (
        <View style={styles.answersContainer}>
          <Text style={styles.question}>האם יש לך ידע בעזרה ראשונה?</Text>
          <TouchableOpacity
            style={[styles.answer, selectedAnswer === 'מע"ר' && styles.selectedAnswer]}
            onPress={() => handleAnswerSelect('מע"ר')}
          >
            <Text style={styles.answerText}>מע"ר</Text>
          </TouchableOpacity>
          {/* Add more answer options for the question */}
        </View>
      )}
    </View>
  );
};

 const styles = StyleSheet.create({
      container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#F5F5F5',
      },
      title: {
        fontSize: 20,
        fontWeight: 'bold',
        marginBottom: 20,
        textAlign: 'center',
        color: '#333',
      },
      subtitle: {
        fontSize: 16,
        marginBottom: 10,
        textAlign: 'center',
        color: '#666',
      },
      option: {
        borderWidth: 1,
        borderColor: '#CCC',
        borderRadius: 10,
        padding: 10,
        marginVertical: 5,
        width: 200,
        alignItems: 'center',
        justifyContent: 'center',
      },
      selectedOption: {
        backgroundColor: '#007AFF',
        borderColor: '#007AFF',
      },
      optionText: {
        fontSize: 16,
        color: '#333',
      },
    });
    
    export default LanguageSelection;
    






